import React from 'react'

const Avatar = ({children, backgroundColor, px ,py, borderRedius, color, fontSize, cursor, }) => {
  const style ={
    backgroundColor,
    padding: `${px} ${py}`,
    color:color || 'Black',
    borderRedius,
    fontSize,
    textAlign: "center",
    
    cursor:cursor || null
  }
  return (
    <div style={style}>
      {children}
    </div>
  )
}

export default Avatar
