import React, {useState} from 'react'
import icon from '../../assets/icon.png'

const Auth = () => {

    const [isSignup, setIsSignup]= useState(true)
    
    const HandleSwitch=() =>{
        setIsSignup(!isSignup)
    }
  return (
  <section className='auth-section'>
    <div className='auth_container'>
        { !isSignup && <img src={icon} alt='Stack Overflow' className='auth-logo' />}
        <form >
         {
            isSignup && (
                <label htmlFor="name">
                    <h3>display Namerd</h3>
                    <input type="text" name='name' id='name'/>
                </label>        
            )
         }
            <label htmlFor="email">
                <h3>Email</h3>
                <input type="email" name='email' id='email' />
            </label>
            <label htmlFor="password">
                <div>
                <h3>Password</h3>
                {!isSignup && <h4>Forgot password?</h4>}                
                </div>
                <input type="password" name='password' id='password' />
                {isSignup && <p>Password contain at least 8 charecters,<br/>including at least 1 letter and 1 number</p>}
            </label>
            {
                isSignup && (
                    <label htmlFor="check">
                       <input type="checkbox"  id='check'/>
                       <p>Opt-in to occetional,product update<br/>user research invitition</p>
                    </label>
                )
            }
        
            <button type='submit' className='auth-btn'>{ isSignup ? 'sign up' : 'Log in' }</button>
        </form>
        <p>{isSignup ? 'Already have an account?' : 'Don\'t have accout ?' }</p>
        <button type='button' className='handle-switch-btn' onClick={HandleSwitch}>{isSignup ? 'Log in' : 'Sign up'}</button>
    </div>

  </section>
  )
}

export default Auth
