import React, {useState} from 'react'
import icon from '../../assets/icon.png'
import AboutAuth from './AboutAuth'
import './Auth.css'

const Auth = () => {

    const [isSignup, setIsSignup]= useState(true)
    
    const HandleSwitch=() =>{
        setIsSignup(!isSignup)
    }
  return (
  <section className='auth-section'>
    {isSignup && <AboutAuth/>}
    <div className='auth_container'>
        { !isSignup && <img src={icon} alt='Stack Overflow' className='auth-logo' />}
        <form >
         {
            isSignup && (
                <label htmlFor="name">
                    <h3>display Namerd</h3>
                    <input type="text" name='name' id='name'/>
                </label>        
            )
         }
            <label htmlFor="email">
                <h3>Email</h3>
                <input type="email" name='email' id='email' />
            </label>
            <label htmlFor="password">
                <div style={{display:""}}>
                <h3>Password</h3>
                {!isSignup && <h4>Forgot password?</h4>}                
                </div>
                <input type="password" name='password' id='password' />
                {isSignup && <p style={{fontSize:"15px"}}>Password contain at least 8 charecters,<br/>including at least 1 letter and 1 number.</p>}
            </label>
            {
                isSignup && (
                    <label htmlFor="check">
                       <input type="checkbox"  id='check'/>
                       <p style={{fontSize:"15px"}}>Opt-in to occetional, product update<br/>user research invitition, company <br/>announc and digests.</p>
                    </label>
                )
            }
        
            <button type='submit' className='auth-btn'>{ isSignup ? 'sign up' : 'Log in' }</button>
            {
                isSignup && (
                    <p style={{color:"black", fontSize:"13px"}}>
                        By clicking "Sign UP" you agree to the 
                        <span style={{color:"#007ac6"}}>Term <br/>of Condition</span>, 
                        <span style={{color:"#007ac6"}}>Privecy policy</span> and
                        <span style={{color:"#007ac6"}}> Cokies policy</span>
                    </p>
                )
            }
        </form>
        <p>{isSignup ? 'Already have an account?' : 'Don\'t have accout ?' }</p>
        <button type='button' className='handle-switch-btn' onClick={HandleSwitch}>{isSignup ? 'Log in' : 'Sign up'}</button>

    </div>

  </section>
  )
}

export default Auth
